import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard"); // O "/products" seg√∫n el rol guardado
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos
    console.log("üîç Iniciando sesi√≥n con:", { email, password });

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      console.log("üì° Respuesta del servidor recibida");
      const data = await response.json();
      console.log("üì° Datos del servidor:", data);

      if (!response.ok) {
        throw new Error(data.message || "Error al iniciar sesi√≥n");
      }

      console.log("‚úÖ Inicio de sesi√≥n exitoso. Token recibido:", data.token);
      // Guardar el token en localStorage
      localStorage.setItem("token", data.token);

      // Redirigir seg√∫n el rol
      if (data.role === "admin") {
        console.log("üîÄ Redirigiendo a /dashboard");
        navigate("/Dashboard")
      } else {
        console.log("üîÄ Redirigiendo a /products");
        navigate("/Products");
      }
    } catch (error) {
      console.error("‚ùå Error al iniciar sesi√≥n:", error.message);
      setError(error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-96">
        <h2 className="text-2xl font-semibold text-center mb-6">Iniciar Sesi√≥n</h2>

        {error && <p className="text-red-500 text-sm mb-4">{error}</p>}

        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Correo electr√≥nico</label>
            <input
              type="email"
              className="w-full p-2 border rounded-lg"
              placeholder="admin@negocio.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Contrase√±a</label>
            <input
              type="password"
              className="w-full p-2 border rounded-lg"
              placeholder="********"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition"
          >
            Iniciar Sesi√≥n
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
