import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaUser, FaLock } from "react-icons/fa"; // √çconos para mejorar el dise√±o
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard"); // O "/products" seg√∫n el rol guardado
    }
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos
    console.log("üîç Iniciando sesi√≥n con:", { email, password });

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      console.log("üì° Respuesta del servidor recibida");
      const data = await response.json();
      console.log("üì° Datos del servidor:", data);

      if (!response.ok) {
        throw new Error(data.message || "Error al iniciar sesi√≥n");
      }

      console.log("‚úÖ Inicio de sesi√≥n exitoso. Token recibido:", data.token);
      // Guardar el token en localStorage
      localStorage.setItem("token", data.token);

      // Redirigir seg√∫n el rol
      if (data.role === "admin") {
        console.log("üîÄ Redirigiendo a /dashboard");
        navigate("/Dashboard");
      } else {
        console.log("üîÄ Redirigiendo a /products");
        navigate("/Products");
      }
    } catch (error) {
      console.error("‚ùå Error al iniciar sesi√≥n:", error.message);
      setError(error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Iniciar Sesi√≥n</h2>

        {error && <p className="error-message">{error}</p>}

        <form onSubmit={handleLogin}>
          <div className="input-group">
            <FaUser className="input-icon" />
            <input
              type="email"
              className="input-field"
              placeholder="admin@negocio.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <FaLock className="input-icon" />
            <input
              type="password"
              className="input-field"
              placeholder="********"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="login-button">Iniciar Sesi√≥n</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
