import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaUser, FaLock } from "react-icons/fa"; // Íconos para mejorar el diseño

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard"); // O "/products" según el rol guardado
    }
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos
    console.log("🔍 Iniciando sesión con:", { email, password });

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      console.log("📡 Respuesta del servidor recibida");
      const data = await response.json();
      console.log("📡 Datos del servidor:", data);

      if (!response.ok) {
        throw new Error(data.message || "Error al iniciar sesión");
      }

      console.log("✅ Inicio de sesión exitoso. Token recibido:", data.token);
      // Guardar el token en localStorage
      localStorage.setItem("token", data.token);

      // Redirigir según el rol
      if (data.role === "admin") {
        console.log("🔀 Redirigiendo a /dashboard");
        navigate("/Dashboard");
      } else {
        console.log("🔀 Redirigiendo a /products");
        navigate("/Products");
      }
    } catch (error) {
      console.error("❌ Error al iniciar sesión:", error.message);
      setError(error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-blue-700 p-4">
      <div className="bg-white p-10 rounded-2xl shadow-2xl w-full max-w-md text-center">
        <h2 className="text-3xl font-bold text-blue-700 mb-6">Iniciar Sesión</h2>

        {error && <p className="text-red-500 text-sm mb-4">{error}</p>}

        <form onSubmit={handleLogin}>
          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">Correo electrónico</label>
            <div className="flex items-center border rounded-lg p-3 bg-gray-100 focus-within:bg-white focus-within:ring-2 focus-within:ring-blue-500">
              <FaUser className="text-gray-500 mr-2" />
              <input
                type="email"
                className="w-full bg-transparent focus:outline-none"
                placeholder="admin@negocio.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">Contraseña</label>
            <div className="flex items-center border rounded-lg p-3 bg-gray-100 focus-within:bg-white focus-within:ring-2 focus-within:ring-blue-500">
              <FaLock className="text-gray-500 mr-2" />
              <input
                type="password"
                className="w-full bg-transparent focus:outline-none"
                placeholder="********"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-800 transition duration-300"
          >
            Iniciar Sesión
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
