import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";

const Dashboard = () => {
  const navigate = useNavigate();
  const [role, setRole] = useState("");

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    if (!userRole) {
      navigate("/");
    }
    setRole(userRole);
  }, [navigate]);

  return (

    <div className="min-h-screen bg-gray-100">
      <Navbar />
    
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-4">Panel de Control</h1>
      
      {role === "admin" ? (
        <div className="grid grid-cols-2 gap-4">
          <button 
            onClick={() => navigate("/users")} 
            className="bg-blue-500 text-white p-4 rounded-lg shadow-md text-lg"
          >
            Gestionar Usuarios
          </button>
          <button 
            onClick={() => navigate("/products")} 
            className="bg-green-500 text-white p-4 rounded-lg shadow-md text-lg"
          >
            Gestionar Productos
          </button>
          <button 
            onClick={() => navigate("/orders")} 
            className="bg-yellow-500 text-white p-4 rounded-lg shadow-md text-lg"
          >
            Gestionar Pedidos
          </button>
        </div>
      ) : (
        <div className="text-center">
          <h2 className="text-xl font-semibold">Bienvenido Empleado</h2>
          <p className="text-gray-600">Solo puedes consultar productos y pedidos.</p>
          <div className="mt-4">
            <button 
              onClick={() => navigate("/products")} 
              className="bg-green-500 text-white p-4 rounded-lg shadow-md text-lg"
            >
              Ver Productos
            </button>
            <button 
              onClick={() => navigate("/orders")} 
              className="bg-yellow-500 text-white p-4 rounded-lg shadow-md text-lg ml-4"
            >
              Ver Pedidos
            </button>
          </div>
        </div>
      )}
    </div>
    </div>
  );
};

export default Dashboard;
